$(function () { $("#header, footer a").on("click", function (event) { event.preventDefault(); let hash = this.hash; $("body,html").animate({ scrollTop: $(hash).offset().top }, 1500, function () { window.location.hash = hash }) }) }); $(".carousel-inner img").on('click', function () { $(this).animate({ width: "800px", }, 1000) }); $(".carousel-inner img").mouseleave(function () { $(this).animate({ width: "450px", }, 900) })